import React, { useState } from "react";
import axios from "axios";

function App() {
  const [prompt, setPrompt] = useState("");
  const [audioUrl, setAudioUrl] = useState(null);
  const [duration, setDuration] = useState(30);
  const [vocals, setVocals] = useState(true);
  const [selectedFile, setSelectedFile] = useState(null);

  const handleGenerate = async () => {
    try {
      const response = await axios.post(
        "/api/generate",
        { prompt, duration, vocals },
        { responseType: "blob" }
      );
      const audioBlob = new Blob([response.data], { type: "audio/mpeg" });
      setAudioUrl(URL.createObjectURL(audioBlob));
    } catch (err) {
      console.error("Error generating audio:", err);
    }
  };

  const handleRemix = async () => {
    if (!selectedFile) return;
    const formData = new FormData();
    formData.append("file", selectedFile);
    formData.append("prompt", prompt);
    formData.append("duration", duration);
    formData.append("vocals", vocals);

    try {
      const response = await axios.post("/api/remix", formData, {
        responseType: "blob",
      });
      const audioBlob = new Blob([response.data], { type: "audio/mpeg" });
      setAudioUrl(URL.createObjectURL(audioBlob));
    } catch (err) {
      console.error("Error remixing file:", err);
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>ðŸŽµ AI Music Studio</h2>
      <input
        type="text"
        placeholder="Prompt (bv. techno elfendans)"
        value={prompt}
        onChange={(e) => setPrompt(e.target.value)}
        style={{ width: "300px", marginRight: "10px" }}
      />
      <input
        type="number"
        min="5"
        max="120"
        value={duration}
        onChange={(e) => setDuration(parseInt(e.target.value))}
        style={{ width: "80px", marginRight: "10px" }}
      />
      <label>
        <input
          type="checkbox"
          checked={vocals}
          onChange={(e) => setVocals(e.target.checked)}
        />
        Met zang
      </label>
      <br />
      <button onClick={handleGenerate}>Genereer Muziek</button>
      <br />
      <input
        type="file"
        accept="audio/*"
        onChange={(e) => setSelectedFile(e.target.files[0])}
      />
      <button onClick={handleRemix}>Maak Remix</button>
      <br />
      {audioUrl && (
        <div style={{ marginTop: "20px" }}>
          <audio controls src={audioUrl}></audio>
        </div>
      )}
    </div>
  );
}

export default App;
